import React, { Component } from "react";
import "./App.styles.css";
import PollingScreen from "./components/PollingScreen";

class App extends Component{

    constructor(){
        super();
        this.state= {
            chosenOption: 0
        }
    }


    render(){
        return(
            <PollingScreen chosenOption={this.state.chosenOption}/>
        );
    }

};

export default App;
