import React, { Component } from "react";
import "./PollingScreen.styles.css";

class PollingScreen extends Component {
    constructor(){
        this.props();

    }

    onSubmit = (e)=>{

        e.preventDefault();
        this.props.onSelection(e);

        console.log(e);
    }
    onChange = (e)=>{
        console.log(e.target.value);

    }
    render(){
    return(
        <div className="container">
            <div className="polling-list">
            <h2>Which is your favourite framework?</h2>
            <form onSubmit={this.onSubmit}>
                <div className="option"><label><input type="radio" 
                                                value="Vanilla JS" 
                                                onChange={this.onChange} />Vanilla JS</label></div>
                <div className="option"><label><input type="radio" 
                                                      value="Angular JS" 
                                                      onChange={this.onChange}/>Angular JS</label></div>
                <div className="option"><label><input type="radio" 
                                                      value="React JS" 
                                                      onChange={this.onChange}/>React JS</label></div>
                <div className="option"><label><input type="radio" 
                                                      value="Vue JS" 
                                                      onChange={this.onChange}/>Vue JS</label></div>
            </form>
            </div>
            <button type="submit">Submit Vote!</button>
        </div>
    )}
}



export default PollingScreen;
