import React from "react";
import "./PollingScreen.styles.css";

const PollingScreen = (props)=>{

    const onSubmit = (e)=>{

        this.props.onSelection(e);
    }
    const onChange = (e)=>{
        console.log(e.target.value);

    }

    return(
        <div className="container">
            <div className="polling-list">
            <h2>Which is your favourite framework?</h2>
            <form onSubmit={onSubmit}>
                <div className="option"><label><input type="radio" value="Vanilla JS" onChange={onChange} />Vanilla JS</label></div>
                <div className="option"><label><input type="radio" value="Angular JS" onChange={onChange}/>Angular JS</label></div>
                <div className="option"><label><input type="radio" value="React JS" onChange={onChange}/>React JS</label></div>
                <div className="option"><label><input type="radio" value="Vue JS" onChange={onChange}/>Vue JS</label></div>

            </form>
            </div>
            <button type="submit">Submit Vote!</button>
        </div>
    )
}



export default PollingScreen;
